AllBlips = {}
AllArea = {}

ESX = exports["es_extended"]:getSharedObject()

RegisterNetEvent('esx:playerLoaded')
AddEventHandler('esx:playerLoaded', function(xPlayer)
    ESX.PlayerData = xPlayer
    refreshBlips()
end)

RegisterNetEvent('esx:setJob')
AddEventHandler('esx:setJob', function(job)
    ESX.PlayerData.job = job
    refreshBlips()
end)


function refreshBlips()
    for i, ricon in pairs(AllBlips) do
		RemoveBlip(ricon)
    end
    for i, rarea in pairs(AllArea) do
		RemoveBlip(rarea)
    end
    
    for _, icon in pairs(fCoreBlips) do
        if (icon.Job == "ALL" and icon.Job2 == "ALL") or (ESX.PlayerData.job.name == icon.Job or ESX.PlayerData.job2.name == icon.Job2) then
            icon.blip = AddBlipForCoord(icon.X, icon.Y, icon.Z)
            SetBlipSprite(icon.blip, icon.Id)
            SetBlipDisplay(icon.blip, 4)
            SetBlipScale(icon.blip, icon.BSize)
            SetBlipColour(icon.blip, icon.BColor)
            SetBlipAsShortRange(icon.blip, true)
            BeginTextCommandSetBlipName("STRING")
            AddTextComponentString(icon.Label)
            EndTextCommandSetBlipName(icon.blip)
            table.insert(AllBlips, icon.blip)
        end
    end

    for __,Area in pairs(fCoreBlips) do	
        if (Area.Area) then
            if (Area.Job == "ALL" and Area.Job2 == "ALL") or (ESX.PlayerData.job.name == Area.Job or ESX.PlayerData.job2.name == Area.Job2) then
                Area.blip = AddBlipForRadius(Area.X, Area.Y, Area.Z, Area.ASize)
                SetBlipSprite(Area.blip, 1)
                SetBlipColour(Area.blip, Area.AColor)
                SetBlipAlpha(Area.blip, 75)
                table.insert(AllArea, Area.blip)
            end
        end			
    end
end

Citizen.CreateThread(function()
    Citizen.Wait(1000)
    refreshBlips()
end)
